import React from "react";
import { Trash2 } from "lucide-react";

const DeliveryPointsInput = ({ deliveryPoints, setDeliveryPoints, depot, setDepot }) => {
  const handleRemovePoint = (indexToRemove) => {
    const updatedPoints = deliveryPoints.filter((_, index) => index !== indexToRemove);
    setDeliveryPoints(updatedPoints); // Update the delivery points
  };

  const handleClearDepot = () => {
    setDepot(null); // Clear the depot
  };

  return (
    <div className="mb-6 p-6 bg-white shadow-lg rounded-lg border border-gray-200">
      <h3 className="text-2xl font-bold text-blue-600 mb-6">Depot & Delivery Points</h3>

      {/* Depot Section */}
      {depot ? (
        <div className="p-4 bg-gray-50 shadow-md rounded-md border border-gray-300 flex justify-between items-center mb-6">
          <div>
            <p className="text-lg text-blue-500">
              <span className="font-semibold">Depot Latitude:</span> {depot.lat}
            </p>
            <p className="text-lg text-blue-500">
              <span className="font-semibold">Depot Longitude:</span> {depot.lng}
            </p>
          </div>
          <button
            onClick={handleClearDepot}
            className="text-red-500 hover:text-red-700"
          >
            <Trash2 size={24} />
          </button>
        </div>
      ) : (
        <p className="text-lg text-gray-600 text-center mb-6">
          Click on the map to select a depot.
        </p>
      )}

      {/* Delivery Points Section */}
      {deliveryPoints.length > 0 ? (
        <div className="space-y-4">
          {deliveryPoints.map((point, index) => (
            <div
              key={index}
              className="p-4 bg-gray-50 shadow-md rounded-md border border-gray-300 flex justify-between items-center"
            >
              <div>
                <p className="text-lg text-gray-700">
                  <span className="font-semibold text-blue-500">Latitude:</span> {point.lat}
                </p>
                <p className="text-lg text-gray-700">
                  <span className="font-semibold text-blue-500">Longitude:</span> {point.lng}
                </p>
              </div>
              <button
                onClick={() => handleRemovePoint(index)}
                className="text-red-500 hover:text-red-700"
              >
                <Trash2 size={24} />
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-lg text-gray-600 text-center">
          Click on the map to add delivery points.
        </p>
      )}
    </div>
  );
};

export default DeliveryPointsInput;
