import React from "react";
import { MapContainer, TileLayer, Marker, Polyline } from "react-leaflet";
import { useLocation } from "react-router-dom";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Custom icons
const depotIcon = L.icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/167/167707.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const deliveryPointIcon = L.icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/167/167729.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const OptimalRoutePage = () => {
  const location = useLocation();
  const { route } = location.state || {}; // Access the route passed via state

  if (!route || route.length === 0) {
    return <p>No route data available.</p>;
  }

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 mt-6 w-[90vw] h-[85vh]">
      <MapContainer
        center={route[0]} // Center the map on the first point
        zoom={13}
        className="h-full w-full"
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        {/* Depot Marker */}
        <Marker position={route[0]} icon={depotIcon} />
        {/* Delivery Points Markers */}
        {route.slice(1).map((point, index) => (
          <Marker key={index} position={point} icon={deliveryPointIcon} />
        ))}
        {/* Route as a Red Polyline */}
        <Polyline positions={route} color="red" weight={4} />
      </MapContainer>
    </div>
  );
};

export default OptimalRoutePage;
